graphql.query:
query {
  project(id: "e25b927b-4a9f-4be7-8554-54145a2cf1d8") {
    id
    name
    description
    createdAt
    environments {
      edges {
        node {
          id
          name
          createdAt
          deployments {
            edges {
              node {
                id
                status
                createdAt
                url
              }
            }
          }
        }
      }
    }
  }
}
